$(document).ready(function(){  
	$('.range-slider').slick({
		slidesToShow: 1,
		slidesToScroll: 1,
		arrows: true,
		adaptiveHeight: true,
		fade: true,
		speed: 300
	});

	var curSlide = $('.nav-number .current'),
		totalSlide = $('.nav-number .total'),
		totalSlideCount = $('.range-slider__item').length;
		totalSlide.text(totalSlideCount);

	$('.range-slider').on('afterChange', function(event, slick, currentSlide){
  	curSlide.text(currentSlide + 1);
	});
});